// Extract and prepare user data for GSuite Admin node
const data = $input.first().json.body;
const employee = data.employee;
const config = data.config || {};

// Parse full name into first and last name
const nameParts = employee.fullName.split(' ');
const firstName = nameParts[0];
const lastName = nameParts.slice(1).join(' ') || nameParts[0];

// Generate secure temporary password
const tempPassword = 'Welcome' + Math.floor(Math.random() * 10000) + '!@#';

return [{
  json: {
    requestId: data.requestId,
    primaryEmail: employee.workEmail,
    firstName: firstName,
    lastName: lastName,
    fullName: employee.fullName,
    password: tempPassword,
    orgUnitPath: config.primaryOrgUnit || '/',
    department: employee.department || '',
    jobTitle: employee.jobTitle || '',
    groups: config.groups || [],
    tempPassword: tempPassword
  }
}];

// Split groups for individual processing with GSuite Admin addToGroup
const groups = $('prepare1').first().json.groups;
const userEmail = $('prepare1').first().json.primaryEmail;
const createdUser = $('createUser1').first().json;

if (!groups || groups.length === 0) {
  return [];
}

// Return each group as a separate item for GSuite Admin addToGroup operation
return groups.map(groupEmail => ({
  json: {
    userId: userEmail,
    groupId: groupEmail,
    userInfo: createdUser
  }
}));

// Format successful user creation with groups
const originalData = $('prepare1').first().json;
const userCreation = $('createUser1').first().json;

// Process group results from GSuite Admin addToGroup operations
let groupResults = [];
try {
  const groupItems = $('addToGroup1').all();
  groupResults = groupItems.map((item, index) => {
    const success = item.json && !item.json.error;
    return {
      group: originalData.groups[index] || 'unknown',
      status: success ? 'added' : 'failed',
      error: !success ? (item.json?.error?.message || 'Unknown error') : null
    };
  });
} catch (e) {
  // No groups processed
}

const failedGroups = groupResults.filter(g => g.status === 'failed').length;
let status = 'success';
let message = 'Google Workspace user created successfully';

if (failedGroups > 0) {
  status = 'partial';
  message = `User created but ${failedGroups} group(s) failed`;
}

return [{
  json: {
    appName: 'googleWorkspace',
    requestId: originalData.requestId,
    result: {
      status: status,
      userId: userCreation.id,
      primaryEmail: userCreation.primaryEmail,
      message: message,
      details: {
        user: {
          id: userCreation.id,
          primaryEmail: userCreation.primaryEmail,
          name: userCreation.name,
          orgUnitPath: userCreation.orgUnitPath,
          suspended: userCreation.suspended
        },
        orgUnit: userCreation.orgUnitPath,
        groups: groupResults,
        temporaryPassword: originalData.tempPassword,
        created: userCreation.creationTime || new Date().toISOString()
      }
    }
  }
}];

// Format successful user creation without groups
const originalData = $('prepare1').first().json;
const userCreation = $('createUser1').first().json;

return [{
  json: {
    appName: 'googleWorkspace',
    requestId: originalData.requestId,
    result: {
      status: 'success',
      userId: userCreation.id,
      primaryEmail: userCreation.primaryEmail,
      message: 'Google Workspace user created successfully',
      details: {
        user: {
          id: userCreation.id,
          primaryEmail: userCreation.primaryEmail,
          name: userCreation.name,
          orgUnitPath: userCreation.orgUnitPath,
          suspended: userCreation.suspended
        },
        orgUnit: userCreation.orgUnitPath,
        groups: [],
        temporaryPassword: originalData.tempPassword,
        created: userCreation.creationTime || new Date().toISOString()
      }
    }
  }
}];

// Handle user creation failure
const originalData = $('prepare1').first().json;
const userCreation = $('createUser1').first().json;
const error = userCreation.error || 'Unknown error';

return [{
  json: {
    appName: 'googleWorkspace',
    requestId: originalData.requestId,
    result: {
      status: 'error',
      userId: null,
      primaryEmail: originalData.primaryEmail,
      message: 'Failed to create Google Workspace user',
      details: {
        user: null,
        orgUnit: originalData.orgUnitPath,
        groups: [],
        temporaryPassword: null,
        created: null
      },
      error: {
        message: error.message || 'User creation failed',
        details: error
      }
    }
  }
}];
