// Extract and validate incoming data
const requestData = $input.first().json;

// Validate required fields
if (!requestData.employee?.workEmail || !requestData.employee?.fullName) {
  throw new Error('Missing required employee information');
}

// Generate request ID if not provided
const requestId = requestData.requestId || `prov-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

// Prepare data for parallel processing
const enabledApps = [];
const applications = requestData.applications || {};

for (const [appName, config] of Object.entries(applications)) {
  if (config.enabled) {
    enabledApps.push({
      appName,
      config,
      employee: requestData.employee,
      requestId
    });
  }
}

return enabledApps.map(app => ({ json: app }));

// Format Google Workspace response for orchestrator
const response = $input.first().json;

// The Google Workspace workflow returns this format:
// { appName: 'googleWorkspace', requestId: 'xxx', result: { ... } }
// We need to pass it through in the expected format

return [{
  json: {
    appName: response.appName || 'googleWorkspace',
    result: response.result
  }
}];

// Mock Slack provisioning
const data = $input.first().json;

return [{
  json: {
    appName: 'slack',
    result: {
      status: 'success',
      userId: data.employee.workEmail,
      message: 'Slack account created',
      details: {
        channels: data.config.channels || ['general'],
        role: data.config.userRole || 'regular'
      }
    }
  }
}];

// Aggregate all results
const allItems = $input.all();
const requestData = $('webhook-trigger').first().json;
const requestId = requestData.requestId || 'unknown';

const results = {};
let overallStatus = 'success';
const errors = [];

// Process each result
for (const item of allItems) {
  const data = item.json;
  if (data.appName && data.result) {
    results[data.appName] = data.result;

    if (data.result.status === 'error' || data.result.status === 'failed') {
      overallStatus = 'partial';
      errors.push(`${data.appName}: ${data.result.error || 'Unknown error'}`);
    }
  }
}

// If all failed, mark as failed
if (errors.length === Object.keys(results).length && errors.length > 0) {
  overallStatus = 'failed';
}

return [{
  json: {
    requestId,
    status: overallStatus,
    timestamp: new Date().toISOString(),
    results,
    errors: errors.length > 0 ? errors : undefined,
    summary: {
      requested: Object.keys(requestData.applications || {}).length,
      successful: Object.values(results).filter(r => r.status === 'success').length,
      failed: Object.values(results).filter(r => r.status === 'error' || r.status === 'failed').length
    }
  }
}];
